                            url = url + `&${v}=${temp[v]}`;
                        }
                        break;
                    case "PAGEWIDTH":
                    case "PAGEHEIGHT":
                        if (this.selected_page_size != "Auto") {
                            url = url + `&${v}=${temp[v]}`;
                        }
                        break;
                    default:
                        if (temp[v] != 0) {
                            if (this.reg_mark_chkbox == false) { }
                            url = url + `&${v}=${temp[v]}`;
                        }
                        break;
                }
            });
            this.box_outline_preview = `https://templatemaker-dev.signcut.com?REQUEST=DIELINESPREVIEW&MODEL=${this.boxData.id}&CUSTOMER=whisqu` + url;
