                this.form["PAGEWIDTH"] = paper.width;
                this.form["PAGEHEIGHT"] = paper.height;
            }
        },
        reg_mark_chkbox(v) {
            if (v) {
                this.form["REGISTRATION"] = this.selected_reg_mark;
            } else {
                this.form["REGISTRATION"] = 'none';
            }
        },
        form(v) {
            // alert(v);
        },
