                        if (this.reg_mark_chkbox) {
                            url = url + `&${v}=${temp[v]}`;
                        }
                        break;
                    case "PAGEWIDTH":
                    case "PAGEHEIGHT":
                        if (this.selected_page_size != "Auto") {
                            url = url + `&${v}=${temp[v]}`;
                        }
                        break;
