                let el = this.form_elements_inline.filter(i => i.symbol == v[0])[0];
                    if (el["type"] == "measure") {
                        this.form[v[0]] = (to == "mm" ? Math.round((v[1] * operation[to])): Math.round((v[1] * operation[to])*100) / 100);
                    }
            }));
        }
    }
